document.addEventListener("DOMContentLoaded",function(){const navbar=document.querySelector(".navbar");const navLinks=document.querySelectorAll(".nav-link");const sections=document.querySelectorAll("section[id]");const themeToggle=document.querySelector(".theme-toggle");const heroStats=document.querySelectorAll(".stat-number");const skillTabs=document.querySelectorAll(".skill-tab");const skillsGrid=document.querySelector(".skills-grid");const particles=document.querySelector(".particles");const typingElement=document.querySelector(".typing-text");window.addEventListener("scroll",()=>{if(window.scrollY>100){navbar.classList.add("scrolled")}else{navbar.classList.remove("scrolled")}let current="";sections.forEach(section=>{const sectionTop=section.offsetTop;const sectionHeight=section.clientHeight;if(scrollY>=(sectionTop-200)){current=section.getAttribute("id")}});navLinks.forEach(link=>{link.classList.remove("active");if(link.getAttribute("href")==="#"+current){link.classList.add("active")}})});navLinks.forEach(link=>{link.addEventListener("click",e=>{e.preventDefault();const targetId=link.getAttribute("href");const targetSection=document.querySelector(targetId);if(targetSection){targetSection.scrollIntoView({behavior:"smooth"})}})});if(themeToggle){themeToggle.addEventListener("click",()=>{const currentTheme=document.documentElement.getAttribute("data-theme");const newTheme=currentTheme==="light"?"dark":"light";document.documentElement.setAttribute("data-theme",newTheme);localStorage.setItem("theme",newTheme)})}const savedTheme=localStorage.getItem("theme");if(savedTheme){document.documentElement.setAttribute("data-theme",savedTheme)}function animateCounter(element,target,duration=2000){let start=0;const increment=target/(duration/16);const timer=setInterval(()=>{start+=increment;if(start>=target){element.textContent=target.toLocaleString();clearInterval(timer)}else{element.textContent=Math.floor(start).toLocaleString()}},16)}const observerOptions={threshold:0.5,rootMargin:"0px 0px -100px 0px"};const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){const target=entry.target;if(target.classList.contains("stat-number")){const finalValue=parseInt(target.getAttribute("data-count")||target.textContent.replace(/,/g,""));animateCounter(target,finalValue);observer.unobserve(target)}}})},observerOptions);heroStats.forEach(stat=>{observer.observe(stat)});const skillsData={programming:[{name:"Python",level:"Advanced"},{name:"JavaScript",level:"Intermediate"},{name:"PHP",level:"Intermediate"},{name:"HTML/CSS",level:"Advanced"},{name:"SQL",level:"Intermediate"},{name:"C++",level:"Beginner"}],frameworks:[{name:"React",level:"Intermediate"},{name:"Node.js",level:"Beginner"},{name:"Flask",level:"Intermediate"},{name:"Bootstrap",level:"Advanced"},{name:"jQuery",level:"Intermediate"},{name:"Git",level:"Intermediate"}],soft:[{name:"Problem Solving",level:"Advanced"},{name:"Team Leadership",level:"Advanced"},{name:"Communication",level:"Intermediate"},{name:"Project Management",level:"Intermediate"},{name:"Mentoring",level:"Advanced"},{name:"Innovation",level:"Advanced"}]};function displaySkills(category){const skills=skillsData[category];skillsGrid.innerHTML=`<div class="skill-category"><h3>${category.charAt(0).toUpperCase()+category.slice(1)} ${category==="programming"?"Languages":category==="frameworks"?"& Tools":"Skills"}</h3>${skills.map(skill=>`<div class="skill-item"><span class="skill-name">${skill.name}</span><span class="skill-level">${skill.level}</span></div>`).join("")}</div>`}skillTabs.forEach(tab=>{tab.addEventListener("click",()=>{skillTabs.forEach(t=>t.classList.remove("active"));tab.classList.add("active");const category=tab.getAttribute("data-category");displaySkills(category)})});if(skillTabs.length>0){skillTabs[0].click()}function createParticles(){if(!particles)return;for(let i=0;i<50;i++){const particle=document.createElement("div");particle.classList.add("particle");particle.style.left=Math.random()*100+"%";particle.style.top=Math.random()*100+"%";particle.style.animationDelay=Math.random()*6+"s";particle.style.animationDuration=(Math.random()*3+3)+"s";particles.appendChild(particle)}}createParticles();const typingTexts=["Future","Kenya","Innovation"];let textIndex=0;let charIndex=0;let isDeleting=false;function typeWriter(){if(!typingElement)return;const currentText=typingTexts[textIndex];if(isDeleting){typingElement.textContent=currentText.substring(0,charIndex-1);charIndex--}else{typingElement.textContent=currentText.substring(0,charIndex+1);charIndex++}let typeSpeed=isDeleting?50:100;if(!isDeleting&&charIndex===currentText.length){typeSpeed=2000;isDeleting=true}else if(isDeleting&&charIndex===0){isDeleting=false;textIndex=(textIndex+1)%typingTexts.length;typeSpeed=500}setTimeout(typeWriter,typeSpeed)}typeWriter();const contactForm=document.querySelector(".contact-form");if(contactForm){contactForm.addEventListener("submit",e=>{e.preventDefault();const formData=new FormData(contactForm);const name=formData.get("name");const email=formData.get("email");const subject=formData.get("subject");const message=formData.get("message");console.log("Form submitted:",{name,email,subject,message});alert("Thank you for your message! I'll get back to you soon.");contactForm.reset()})}});

